CREATE TABLE category (id_category BIGINT AUTO_INCREMENT, category_name VARCHAR(100) DEFAULT '' NOT NULL, PRIMARY KEY(id_category)) ENGINE = INNODB;
CREATE TABLE contacts (id_contact INT AUTO_INCREMENT, email VARCHAR(255) DEFAULT '' NOT NULL, PRIMARY KEY(id_contact)) ENGINE = INNODB;
CREATE TABLE users (id_user INT AUTO_INCREMENT, email VARCHAR(255) DEFAULT '' NOT NULL, password VARCHAR(55) DEFAULT '' NOT NULL, first_name VARCHAR(55) DEFAULT '' NOT NULL, last_name VARCHAR(55) DEFAULT '' NOT NULL, city VARCHAR(55) DEFAULT '' NOT NULL, security_code VARCHAR(255) DEFAULT '' NOT NULL, PRIMARY KEY(id_user)) ENGINE = INNODB;
